import { useState } from "react";

function is_null(data) {
  return data === "" || data === 0;
}

function verifierData(data) {
  if (data === "" || data === 0) {
    return "Pas null";
  } else if (data > 1) {
    return "sup 1";
  } else {
    return "inf 1";
  }
}

function Myelancement(props) {
  return (
    <p>
      {props.longueur / props.largeur < 4
        ? "Le bâtiment ne respecte pas la condition d'élancement"
        : "Le bâtiment respecte la condition d'élancement"
      }
    </p>
  )
}

function Myelancement_bis(props) {
  var longueur = props.longueur
  var largeur = props.largeur
  if (longueur === "" || longueur === 0 
  || largeur === "" || largeur === 0) {
    var message = "Attention on ne connait pas lelancement !";
  } else if (longueur / largeur >= 4) {
    var message = "Le bâtiment respecte la condition d'élancement";
  } else {
    var message = "Le bâtiment ne respecte pas la condition d'élancement";
  }
  return message
}

function MyComponent() {

  const [elanc, setElanc] = 
    useState ("Attention on ne connait pas l'élancement !")

  const setElancement = (event) => {
    var longueur = document.getElementById ("Long").value;
    var largeur = document.getElementById ("Larg").value;
    setElanc (Myelancement_bis (longueur={longueur}, largeur={largeur}))
  }
   return (
        <div id="calcul_elancement">
      Ici a lieu le calcul de l'élancement du bâtiment:<br/><br/><br/>
        <label for="name">Longueur du bâtiment :</label>
        <input type="number" id="Long" name="Longueur" onChange={setElancement}/> <br/> <br/>
        <label for="name">Largeur du bâtiment :</label>
        <input type="number" id="Larg" name="Largeur" onChange={setElancement}/><br/> <br/>
        <p>La conclusion sur l'élancement est  : <br/> <br/>  { elanc }</p>
        </div>);
}

export default MyComponent
