import { useState } from "react";


function my_elancement(props) {
  var longueur = props.longueur
  var largeur = props.largeur
  if (!isNaN (longueur) || !isNaN (largeur) || largeur === 0) {
    var message = "Attention on ne connait pas l'élancement !";
  } else if (longueur / largeur >= 2) {
    var message = "Le bâtiment respecte la condition d'élancement";
  } else {
    var message = "Le bâtiment ne respecte pas la condition d'élancement";
  }
  return message
}

function MyComponent() {

  const [elanc, setElanc] = 
    useState ("Attention on ne connait pas l'élancement !")

  const setElancement = () => {
    var longueur = parseFloat (document.getElementById ("Long").value);
    var largeur = parseFloat (document.getElementById ("Larg").value);
    alert (longueur  + "   "  + largeur)
    setElanc (my_elancement (longueur={ longueur}, largeur={ largeur }));
    }
   return (
        <div id="calcul_elancement">
      Ici a lieu le calcul de l'élancement du bâtiment:<br/><br/><br/>
        <label for="name">Longueur du bâtiment :</label>
        <input type="number" id="Long" name="Longueur" 
               onChange={setElancement}/> <br/> <br/>
        <label for="name">Largeur du bâtiment :</label>
        <input type="number" id="Larg" name="Largeur"
               onChange={setElancement} /><br/> <br/>
        <p>La conclusion sur l'élancement est  : <br/> <br/>  { elanc }</p>
        </div>);
  }

export default MyComponent
