import { useState } from "react";

function is_null(data) {
  return data === "" || data === 0;
}

function verifierData(data) {
  if (data === "" || data === 0) {
    return "Pas null";
  } else if (data > 1) {
    return "sup 1";
  } else {
    return "inf 1";
  }
}

function Myelancement(props) {
  return (
    <p>
      {props.longueur / props.largeur < 4
        ? "Le bâtiment ne respecte pas la condition d'élancement"
        : "Le bâtiment respecte la condition d'élancement"
      }
    </p>
  )
}

function Myelancement_bis(props) {
  if (props.largeur === "" || props.largeur === 0) {
    return "Attention vous divisez par 0";
  } else if (props.longueur / props.largeur >= 4) {
    return "Le bâtiment respecte la condition d'élancement";
  } else {
    return "Le bâtiment ne respecte pas la condition d'élancement";
  }
}

function MyComponent() {
 /*  const [elanc, setElanc] = useState ("Elancement inconnu !")

  const setElancement = (props) => {
    setElanc ()
  } */
   return (
        <div id="calcul_elancement"> 
      Ici a lieu le calcul de l'élancement du bâtiment:<br/><br/><br/>
        <label for="name">Longueur du bâtiment:</label>
        <input type="int" id="Long" name="Longueur" onChange={Myelancement_bis}/> <br/> <br/>
        <label for="name">Largeur du bâtiment:</label>
        <input type="int" id="Larg" name="Largeur"/><br/> <br/>
        {/* <label>Elancement: {elanc} </label> */}
        var longueur = document.getElementById("Long").value;
        var largeur = document.getElementById("Larg").value;
        <Myelancement_bis longueur={longueur} largeur={largeur}/>
        </div>);
}

export default MyComponent
